import { observer } from "mobx-react";

import { FormWrapper, login } from "forms";

const LoginModal = observer(({ fields, rootStore, }) => (
  <FormWrapper
    fields={login}
    actions={{
      onSuccess: rootStore.UserStore.loginUser
    }}
    render={form => (
      <CustomModal
        open={open}
        onClose={() => rootStore.ModalStore.closeModal("login")}
        closeOnDimmerClick={false}
        size="mini"
        trigger={
          <Button
            as="a"
            inverted={!fixed}
            onClick={() => rootStore.ModalStore.openModal("login")}
          >
            Войти
          </Button>
        }
        header="Войдите в свой аккаунт!"
        content={<LoginForm form={form} />}
      />
    )}
  />
));

export default LoginModal;
